// File: prob0627.cpp// Computer Systems, Fourth Edition// Problem 6.27#include <iostream>using namespace std;int list[16];int j, numItems;int temp;int main () {   cin >> numItems;   for (j = 0; j < numItems; j++) {      cin >> list[j];   }   temp = list[0];   for (j = 0; j < numItems - 1; j++) {      list[j] = list[j + 1];   }   list[numItems - 1] = temp;   for (j = 0; j < numItems; j++) {      cout << list[j] << ' ';   }   cout << endl;   return 0;}