// File: prob0639.cpp// Computer Systems, Fourth Edition// Figure 6.39#include <iostream>using namespace std;struct node {   int data;   node* next;};int main () {   node *first, *p, *first2, *p2;   int value;   first = 0;   cin >> value;   while (value != -9999) {      p = first;      first = new node;      first->data = value;      first->next = p;      cin >> value;   }   for (p = first; p != 0; p = p->next) {      cout << p->data << ' ';   }   first2 = 0; p2 = 0;   for (p = first; p != 0; p = p->next) {      p2 = first2;      first2 = new node;      first2->data = p->data;      first2->next = p2;   }   for (p2 = first2; p2 != 0; p2 = p2->next) {      cout << p2->data << ' ';   }   return 0;}