// File: prob0637.cpp// Computer Systems, Fourth Edition// Problem 6.37#include <iostream>using namespace std;struct node {   int data;   node* next;};int main () {   node *first, *p;   int value, sum;   first = 0;   cin >> value;   while (value != -9999) {      p = first;      first = new node;      first->data = value;      first->next = p;      cin >> value;   }   for (p = first; p != 0; p = p->next) {      cout << p->data << ' ';   }   sum = 0; p = first;   while (p != 0) {      sum += p->data;      p = p->next;   }   cout << "Sum: " << sum << endl;   return 0;}