// File: prob0638.cpp// Computer Systems, Fourth Edition// Problem 6.38#include <iostream>using namespace std;struct node {   int data;   node* next;};void reverse (node* list) {   if (list != 0) {      reverse (list->next);      cout << list->data << ' ';   }}int main () {   node *first, *p;   int value;   first = 0;   cin >> value;   while (value != -9999) {      p = first;      first = new node;      first->data = value;      first->next = p;      cin >> value;   }   for (p = first; p != 0; p = p->next) {      cout << p->data << ' ';   }   cout << endl;   reverse (first);   return 0;}