// File: prob0629.cpp// Computer Systems, Fourth Edition// Problem 6.29#include <iostream>using namespace std;void getList (int ls[], int& n) {   int j;   cin >> n;   for (j = 0; j < n; j++) {      cin >> ls[j];   }}void putList (int ls[], int n) {   int j;   for (j = 0; j < n; j++) {      cout << ls[j] << ' ';   }   cout << endl;}void rotate (int ls[], int n) {   int j;   int temp;   temp = ls[0];   for (j = 0; j < n - 1; j++) {      ls[j] = ls[j + 1];   }   ls[n - 1] = temp;}int main () {   int list[16];   int numItems;   getList (list, numItems);   putList (list, numItems);   rotate (list, numItems);   putList (list, numItems);   return 0;}