// File: prob0634.cpp// Computer Systems, Fourth Edition// Problem 6.34#include <iostream>using namespace std;int main () {   int guess;   cout << "Pick a number 0..3: ";   cin >> guess;   switch (guess) {      case 0: cout << "Not close"; break;      case 1: cout << "Too low"; break;      case 2: cout << "Right on"; break;      case 3: cout << "Too high"; break;      default: cout << "Illegal input";   }   cout << endl;   return 0;}